import 'react-quill/dist/quill.snow.css'


export const CreatePost = () => {

    const [title, setTitle] = useState('')
    const [summary, setSummary] = useState('')
    const [content, setContent] = useState('')

    const modules = {
        toolbar: [
          [{ header: [1, 2, false] }],
          ['bold', 'italic', 'underline', 'strike', 'blockquote'],
          [
            { list: 'ordered' },
            { list: 'bullet' },
            { indent: '-1' },
            { indent: '+1' },
          ],
          ['link', 'image'],
          ['clean'],
        ],
      };
    return (
       <form>
        <input type='title' placeholder="Enter Title" />
        <input type='summary' placeholder="Enter Blog Summary" />
        <input type='file' placeholder="Select Thumbnail" />
        <ReactQuill value={content} modules={modules} theme={snow}/>
        <button style={{marginTop: '20px'}}>Create Post</button>
       </form>
    )
}

export default CreatePost;
