/* eslint-disable jsx-a11y/alt-text */
/* eslint-disable jsx-a11y/anchor-is-valid */
import { formatISO9075 } from "date-fns";
import { useContext, useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom"
import { UserContext } from "../UserContext";

import FontAwesomeIcon from 'font'

import { Simplesharer } from "simple-sharer";


export const PostPage = () => {
    const [postInfo, setPostInfo] = useState(null)

    const { userInfo } = useContext(UserContext);
    const { id } = useParams();
    useEffect(() => {
        fetch(`http://localhost:4000/post/${id}`)
            .then(response => {
                response.json().then(postInfo => {
                    setPostInfo(postInfo);
                });
            });
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    if (!postInfo) return '';

    const sh = new Simplesharer();
    sh.url = `http://localhost:3000/post/${id}`; //your url


    return (
        <div className="post-page-div">
            <div className="post-page-image">
                <img src={`http://localhost:4000/${postInfo.cover}`} />
            </div>
            <h1 className="post-page-heading">{postInfo.title}</h1>
            <div className="post-page-info">
                <time>{formatISO9075(new Date(postInfo.createdAt))}</time>
                by @{postInfo.author.username}
            </div>
            {/* edit */}
            <Link onClick={() => {
                sh.copy()

                alert('copy to clipboard')

            }}><FontAwesomeIcon icon="far fa-copy" /></Link>
            <Link onClick={() => sh.share("Facebook")}>share on facebook</Link>
            <Link onClick={() => sh.share("tWitter")}>share on twitter</Link>
            <Link onClick={() => sh.share("WHATSAPP")}>share on Whatsapp</Link>


            {userInfo.id === postInfo.author._id &&
                <div className="edit-row">
                    <Link className='edit-btn' to={`/edit/${postInfo._id}`}>Edit this post</Link>
                </div>}

            <div dangerouslySetInnerHTML={{ __html: postInfo.content }} className='content' />




        </div>
    )
}

export default PostPage
