import ReactQuill from 'react-quill'
import { useState } from 'react'
import 'react-quill/dist/quill.snow.css'


const modules = {
    toolbar: [
      [{ header: [1, 2, false] }],
      ['bold', 'italic', 'underline', 'strike', 'blockquote'],
      [
        { list: 'ordered' },
        { list: 'bullet' },
        { indent: '-1' },
        { indent: '+1' },
      ],
      ['link', 'image'],
      ['clean'],
    ],
  };


export const CreatePost = () => {

    const [title, setTitle] = useState('')
    const [summary, setSummary] = useState('')
    const [content, setContent] = useState('')


    return (
       <form>
        <input type='title' placeholder="Enter Title" value={title} onChange={e => setTitle(e.target.value)}/>
        <input type='summary' placeholder="Enter Blog Summary" />
        <input type='file' placeholder="Select Thumbnail" />
        <ReactQuill value={content} modules={modules} theme={'snow'}/>
        <button style={{marginTop: '20px'}}>Create Post</button>
       </form>
    )
}

export default CreatePost;
