/* eslint-disable jsx-a11y/alt-text */
/* eslint-disable jsx-a11y/anchor-is-valid */
import { formatISO9075 } from "date-fns";
import { useContext, useEffect, useState } from "react";
import { useParams, Link, Navigate } from "react-router-dom"
import { UserContext } from "../UserContext";

export const PostPage = () => {
    const [postInfo, setPostInfo] = useState(null)

    //edit
    const [title,setTitle] = useState('');
    const [summary,setSummary] = useState('');
    const [content,setContent] = useState('');
    const [files, setFiles] = useState('');
    const [redirect,setRedirect] = useState(false);

    const { userInfo } = useContext(UserContext);
    const { id } = useParams();
    useEffect(() => {
        fetch(`http://localhost:4000/post/${id}`)
            .then(response => {
                response.json().then(postInfo => {
                    setPostInfo(postInfo);
                });
            });
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    if (!postInfo) return '';


    //edit
    async function deletePost(e) {
        e.preventDefault();
        const data = new FormData();
        data.set('title', title);
        data.set('summary', summary);
        data.set('content', content);
        data.set('id', id);
        if (files?.[0]) {
          data.set('file', files?.[0]);
        }
        const response = await fetch('http://localhost:4000/delete', {
          method: 'DELETE',
          body: data,
          credentials: 'include',
        });
        if (response.ok) {
          setRedirect(true);
        }
      }

      if (redirect) {
        return <Navigate to={'/'} />
      }


    return (
        <div className="post-page">
            <h1 className="title">{postInfo.title}</h1>
            <time>{formatISO9075(new Date(postInfo.createdAt))}</time>
            <div className="author">
                by @{postInfo.author.username}
            </div>
            {userInfo.id === postInfo.author._id &&
                <div className="edit-row">
                    <Link className='edit-btn' to={`/edit/${postInfo._id}`}>Edit this post</Link>
                    <Link to={'/delete'}>Delete</Link>
                </div>}
            <div className="image">
                <img src={`http://localhost:4000/${postInfo.cover}`} />
            </div>
            <div dangerouslySetInnerHTML={{ __html: postInfo.content }} className='content' />
        </div>
    )
}

export default PostPage
